INSERT INTO "financial_item_clause" VALUES('FR Aanvaarding begunstiging Eigen Heerd Is Goud Weerd NV','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;">Eigen Heerd Is Goud Weerd SA, le bénéficiaire acceptant à concurrence de la somme principale et des intérêts qui lui seraient dus en vertu du contrat de prêt pour lequel le présent contrat à été conclus.</p>','fr',1,280,39);
INSERT INTO "financial_item_clause" VALUES('Begunstigde bij overlijden FR 2','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;"><span style=" font-family:''NA Vectora 45,sans-serif''; font-size:8pt; color:#231f20;">Bénéficiaire(s) en cas de décès : </span><span style=" font-family:''NA Vectora 45,sans-serif''; font-size:8pt;">Le conjoint/cohabitant légal du preneur d’assurance, à défaut, les enfants du preneur d’assurance et en cas de représentation leurs descendants, à défaut, les héritiers légaux du preneur d’assurance intervenant conformément à la dévolution légale.</span></p>','fr',1,275,65);
INSERT INTO "financial_item_clause" VALUES('FR Wederzijdse overdracht van rechten','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;">Les preneurs d’assurance déclarent que lors du décès de l’un des preneurs d’assurance tous les droits </p>
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;">sont transmis au preneur d’assurance survivant.</p>','fr',2,286,20);
INSERT INTO "financial_item_clause" VALUES('Begunstigde bij overlijden FR 5','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;"><span style=" font-family:''NA Vectora 45,sans-serif''; font-size:8pt; color:#231f20;">Bénéficiaire(s) en cas de décès : </span><span style=" font-family:''NA Vectora 45,sans-serif''; font-size:8pt;">Autre(s) (nom, prénom, date de naissance, degré de parenté avec le preneur); à défaut, les héritiers légaux du preneur d’assurance intervenant conformément à la dévolution légale.</span></p>','fr',1,278,65);
