INSERT INTO "financial_item_clause" VALUES('Taksactie 2015','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;">Ce versement (cf. point 4.) a été fait dans le cadre de l’action promotionnelle « Action Taxe». Le preneur d''assurance est au courant du fait qu’ en cas de rachat complet ou partiel de la prime ci-dessous (cf. point 4.) endéans les cinq premières années après la date du versement, Patronale Life va récupérer la partie de la taxe (cf. Allocation bonus point 5.) qu’elle a payée du montant de rachat, et ceci sans formalités. Après 5 ans, cet avantage est totalement acquis par le preneur d’assurance.</p>','fr',4,292,30);
INSERT INTO "financial_item_clause" VALUES('NL Taksactie 2015','
<p style=" margin-top:0px; margin-bottom:0px; margin-left:0px; margin-right:0px; -qt-block-indent:0; text-indent:0px;">Deze storting (cf. punt 4.) werd gedaan in het kader van de ''Taksactie''. De verzekeringnemer is ervan op de hoogte dat in het geval van een gehele of gedeeltelijke afkoop van bovenvermelde premie (zie punt 4.) binnen de vijf jaar na de storting ervan, Patronale Life het gedeelte van de verzekeringstaks (Bonus Allocatie vermeld onder punt 5.) dat zij op zich heeft genomen, zonder formaliteiten zal afhouden van de afkoopwaarde. Na 5 jaar is dit voordeel definitief verworven voor de verzekeringsnemer.</p>','nl',4,289,25);
